import React, {useState, useEffect} from "react";
import Pins from "./nyc_ttp_pins.json";
import wheel from "./wheel.gif";

function App(){

    const [pins, setPins] = useState(Pins.slice(0,12));
    const [loading, setLoading] = useState(false);
    const [n, setN] = useState(0);
    useEffect(() => {
        window.addEventListener('scroll', handleScroll);
        return () => window.removeEventListener('scroll', handleScroll); 
    }, []);

    useEffect(() => {
        if (!loading) return;
        loadPins();
    }, [loading])

    function handleScroll() {
        if (window.innerHeight + document.documentElement.scrollTop !== document.documentElement.offsetHeight) return;
        setLoading(true);
    }

    function loadPins() {
        setTimeout(() => {
            if (pins.length < Pins.length) {
            setPins(Pins.slice(0, (pins.length + 12)));
            } else {
                let adding = pins.concat((Pins.slice(n, n+12)));
                setPins(adding);
                console.log(pins)
                if (n >= Pins.length) {
                    setN(0);
                } else {
                    setN(n+12)
                }
            }
            setLoading(false);
        }, 2000);
    }

    return(
        <div>
            {pins.map(pin => (
                <li key={pin.id}><img src={pin.images.["136x136"].url} alt="Pin"/></li>
            ))}
            <img src={wheel} alt="Loading..." />
        </div>
    )
}

export default App;
