import React, {useState, useEffect} from "react";
import Pins from "./nyc_ttp_pins.json";
import wheel from "./wheel.gif";

function App(){

    const [pins, setPins] = useState(Pins.slice(0,2));
    const [loading, setLoading] = useState(false);
    useEffect(() => {
        window.addEventListener('scroll', handleScroll);
        return () => window.removeEventListener('scroll', handleScroll); 
    }, []);

    useEffect(() => {
        if (!loading) return;
        loadPins();
    }, [loading])

    function handleScroll() {
        if (window.innerHeight + document.documentElement.scrollTop !== document.documentElement.offsetHeight) return;
        setLoading(true);
    }

    function loadPins() {
        setTimeout(() => {
            setPins(Pins);
            //fix this to be like loading more pins rather than just the whole list over and over
            setLoading(false);
        }, 2000);
    }

    return(
        <div>
            {pins.map(pin => (
                <li key={pin.id}><img src={pin.images.["136x136"].url} alt="Pin"/></li>
            ))}
            <img src={wheel} alt="Loading..." />
        </div>
    )
}

export default App;
